-- Insertion des animations SnapEvent avec types corrects

-- Vérification après insertion
SELECT COUNT(*) as animations_apres_ajout FROM animations;

-- Statistiques par type d'animation SnapEvent
SELECT 
    type_animation, 
    COUNT(*) as count 
FROM animations 
WHERE type_animation LIKE '%Animation%' OR type_animation LIKE '%Photo%' OR type_animation LIKE '%Vidéo%'
GROUP BY type_animation 
ORDER BY count DESC;

-- Toutes les catégories d'animations
SELECT DISTINCT type_animation 
FROM animations 
ORDER BY type_animation;

-- Exemples d'animations par catégorie
SELECT type_animation, nom, LEFT(description, 80) as description_courte
FROM animations 
WHERE description IS NOT NULL AND description != 'NULL'
ORDER BY type_animation, nom 
LIMIT 20;
